windowclass.script:
function onInit()
	if User.isLocal() then
		speak.setVisible(false);
		portrait.setVisible(false);
		localportrait.setVisible(true);
	end
end

function onDrop(x, y, draginfo)
	if draginfo.isType("deathtimer") then
		ActionDeathTimer.setDyingTimer(getDatabaseNode(), draginfo.getNumberData())
	end
end

string_labeled.script:
function onDrop(x, y, draginfo)
    if draginfo.isType("shortcut") then
        local sClass, sRecord = draginfo.getShortcutData();
        if sClass == "reference_archetype" then
            CharManager2.addCharTypeRef(window.getDatabaseNode(), sClass, sRecord);
        end
    end
end

linkfield.script:
function onInit()
    onValueChanged();
end
function onValueChanged()
    if isEmpty() then
        setVisible(false);
    else
        setVisible(true);
    end
end

string_labeled.script:
function onDrop(x, y, draginfo)
    if draginfo.isType("shortcut") then
        local sClass, sRecord = draginfo.getShortcutData();
        if sClass == "reference_bioform" then
            CharManager2.addCharConsciousnessRef(window.getDatabaseNode(), sClass, sRecord);
        end
    end
end

